import React from "react";
const CommentList = (props) => {
  return (
    <div className="row">
      <div className="col-xl-2 col-lg-2 col-md-2 col-sm-2 ">
        <img
          className="rounded-circle d-block m-auto mt-3"
          style={{ width: "4rem", height: "4rem" }}
          src="https://cdn-icons-png.flaticon.com/512/147/147144.png"
          alt=""
        />
      </div>
      <div className="col-xl-10 col-lg-10 col-md-10 col-sm-10">
        <span className="">
          <b className="fw-bold">Trần văn C</b>{" "}
        </span>{" "}
        <p>
          Giá tiền đi đôi với chất lượng: + Máy đẹp, ngon nghẻ + Các bạn nhân
          viên tư vấn nhiệt tình và đúng theo yêu cầu khách hàng, không phải
          kiểu dí khách mua máy như một số bên khác. Mua cho nhiều anh em bạn bè
          máy ở đây rồi đến giờ vẫn chưa gặp vấn đề gì!
        </p>
        <div>
          <a className="fw-bold text-info">Trả lời</a> <p>19/07/2020 20:06</p>
        </div>
        <hr />
      </div>
    </div>
  );
};

export default CommentList;
