/* eslint-disable jsx-a11y/alt-text */
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Slider from "react-slick";

const Product = () => {
  const [img, setData] = useState([]);
  useEffect(() => {
    const arr = [
      {
        img: "https://lumen.thinkpro.vn//backend/uploads/product/avatar/2021/8/31/hp-340s-g7-thinkpro-thumbnailjpg",
      },
      {
        img: "https://lumen.thinkpro.vn//backend/uploads/product/avatar/2021/9/21/hp-14s-dq2544tu-46m22pa-thinkprojpg",
      },
      {
        img: "https://lumen.thinkpro.vn//backend/uploads/product/avatar/2021/9/17/rog-strix-g513im-hn057t-thinkprojpg",
      },
    ];
    setData(arr);
  }, []);

  const settings = {
    customPaging: function () {
      return (
        <>
          <a>
            <img className="w-100" />
          </a>
        </>
      );
    },
    dots: true,
    dotsClass: "slick-dot slick-thumb mb-5",
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    nextArrow: <SampleNextArrow />,
    prevArrow: <SamplePrevArrow />,
    responsive: [
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          swipeToSlide: true,
          nextArrow: false,
          prevArrow: false,
        },
      },
    ],
  };
  function SampleNextArrow(props) {
    const { className, style, onClick } = props;
    return (
      <div
        className={className}
        style={{
          ...style,
          display: "block",
          background: "rgba(194, 191, 191, 0.911)",
          marginRight: "1rem",
          zIndex: "2",
          borderRadius: "50%",
        }}
        onClick={onClick}
      />
    );
  }

  function SamplePrevArrow(props) {
    const { className, style, onClick } = props;
    return (
      <div
        className={className}
        style={{
          ...style,
          display: "block",
          background: "rgba(194, 191, 191, 0.911)",
          marginLeft: "1rem",
          zIndex: "2",
          borderRadius: "50%",
        }}
        onClick={onClick}
      />
    );
  }

  return (
    <div className="w-75 m-auto d-block">
      <Slider {...settings}>
        {img.map((e) => (
          <div>
            <img className="w-100" src={e.img} />
          </div>
        ))}
      </Slider>
    </div>
  );
};
export default Product;
