import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./productItem.css";
function ProductItem(props) {
  const [imgUrl, setImgUrl] = useState();
  useEffect(() => {
    props.data.HinhAnh.slice(0, 1).map((e) => setImgUrl(e));
  }, [props.data.HinhAnh]);

  return (
    <div
      className="col-xl-3 col-md-4 col-sm-6 col-6 product-box mb-3"
      key={props.data._id}
    >
      <div className="card p-2 product-card ">
        <Link className=" mb-4 product-tag" href="#">
          <div className="img-box w-100">
            <img src={imgUrl.Url} className="w-100" alt="" />
          </div>
          <h5> {props.data.TenSanPham} </h5>
        </Link>
        <div className="card-body ">
          <h5>
            <p>
              Giá từ : <b className="text-danger">200.000vnđ</b>
            </p>
          </h5>
          <p>
            {" "}
            <del>300.000vnđ</del>{" "}
          </p>
          <p>
            <i className="fas fa-gifts text-danger me-2" /> Đang giảm giá
          </p>
        </div>
        <div className="hide ">
          Color: <b className="text-warning"> Gold </b>
          <p />
          <button className="w-100 btn-info btn text-light"> Add cart</button>
        </div>
      </div>
    </div>
  );
}

export default ProductItem;
