/* eslint-disable jsx-a11y/alt-text */
/* eslint-disable no-dupe-keys */
/* eslint-disable jsx-a11y/anchor-is-valid */
import React from "react";
import Slider from "react-slick";

const Slider_home = (props) => {
  const settings = {
    dots: true,
    infinite: true,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    speed: 500,
    autoplaySpeed: 2000,
    cssEase: "linear",
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        },
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 2,
          initialSlide: 2,
          nextArrow: false,
          prevArrow: false,
          dots: false,
        },
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          dots: false,
          nextArrow: false,
          prevArrow: false,
        },
      },
    ],
  };

  return (
    <Slider {...settings}>
      <div className="w-100">
        <a href="#!" className="text-decoration-none">
          <img
            className="rounded-3 w-100"
            src={
              "https://lumen.thinkpro.vn//backend/uploads/banner/2021/9/8/808x288-2%20copy.jpg"
            }
          />
        </a>
      </div>
      <div className="w-100">
        <a href="#!" className="text-decoration-none">
          <img
            className="rounded-3 w-100"
            src={
              "https://lumen.thinkpro.vn//backend/uploads/banner/2021/8/26/808x288%20copy.jpg"
            }
          />
        </a>
      </div>
      <div className="w-100">
        <a href="#!" className="text-decoration-none">
          <img
            className="rounded-3 w-100"
            src={
              "https://lumen.thinkpro.vn//backend/uploads/banner/2021/8/15/KV%20back2school-808x288%20copy.jpg"
            }
          />
        </a>
      </div>
      <div className="w-100">
        <a href="#!" className="text-decoration-none">
          <img
            className="rounded-3 w-100"
            src={
              "https://lumen.thinkpro.vn//backend/uploads/banner/2021/9/7/Trung%20thu%20808x288.jpg"
            }
          />
        </a>
      </div>
    </Slider>
  );
};

export default Slider_home;
