import React from "react";

import InfiniteCarousel from "react-leaf-carousel";

const Saleproduct = () => {
  return (
    <InfiniteCarousel
      breakpoints={[
        {
          breakpoint: 500,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1,
          },
        },
        {
          breakpoint: 800,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 2,
          },
        },
      ]}
      showSides={true}
      sidesOpacity={10}
      sideSize={0}
      slidesToScroll={3}
      slidesToShow={5}
      lazyLoad={true}
    >
      <div class="card p-2 product-card">
        <a class="mb-4 product-tag" href="#">
          <div class="img-box w-100">
            <img
              src="https://lumen.thinkpro.vn//backend/uploads/product/avatar/2020/11/20/swift514_00gold.jpg"
              class="w-100"
              alt=""
            />
          </div>
          <h5>Laptop Acer 55(Chính hãng)</h5>
        </a>
        <div class="card-body">
          <h5>
            <p>
              Giá từ : <b class="text-danger">200.000vnđ</b>
            </p>
          </h5>
          <p>
            <del>300.000vnđ</del>
          </p>

          <p>
            <i class="fas fa-gifts text-danger me-2"></i> Đang giảm giá
          </p>
        </div>

        <div class="hide-slide p-3 w-100">
          <p class="mt-3 bg-light p-3">
            Intel Core i5-1135G7, RAM 8GB, 512GB m.2 NVMe, 14" FHD IPS (1920 x
            1080), Intel Iris Xe, 48Wh, Pink (SF314-59-5178)
          </p>
          <button class="w-100 btn-info btn text-light rounded-pill">
            Thêm vào giỏ hàng
          </button>
        </div>
      </div>
    </InfiniteCarousel>
  );
};

export default Saleproduct;
