/* eslint-disable jsx-a11y/anchor-is-valid */
import { useEffect, useState } from "react";
import axios from "axios";
const Saleproduct = () => {
  const [Data, setData] = useState([]);

  useEffect(() => {
    displayData();
  }, []);
  const displayData = () => {
    const url = "http://localhost:3100/sanpham";
    axios.all([axios.get(url)]).then(
      axios.spread((e) => {
        setData(e.data);
      })
    );
  };

  return (
    <div className="mt-3">
      <div className="container-slider ">
        <h3 className="Head fw-bold ">
          {" "}
          Sản phẩm giảm giá <span className="Arrows"></span>
        </h3>

        <div className="SlickCarousel">
          {/* {Data.map((e)=>  )} */}
          <div className="card p-2 product-card">
            <a className="mb-4 product-tag" href="#">
              <div className="img-box w-100">
                <img
                  src="https://lumen.thinkpro.vn//backend/uploads/product/avatar/2020/11/20/swift514_00gold.jpg"
                  className="w-100"
                  alt=""
                />
              </div>
              <h5>Laptop Acer 55(Chính hãng)</h5>
            </a>
            <div className="card-body">
              <h5>
                <p>
                  Giá từ : <b className="text-danger">200.000vnđ</b>
                </p>
              </h5>
              <p>
                <del>300.000vnđ</del>
              </p>

              <p>
                <i className="fas fa-gifts text-danger me-2"></i> Đang giảm giá
              </p>
            </div>

            <div className="hide-slide p-3 w-100">
              <p className="mt-3 bg-light p-3">
                Intel Core i5-1135G7, RAM 8GB, 512GB m.2 NVMe, 14" FHD IPS (1920
                x 1080), Intel Iris Xe, 48Wh, Pink (SF314-59-5178)
              </p>
              <button className="w-100 btn-info btn text-light rounded-pill">
                Add cart
              </button>
            </div>
          </div>
        </div>
      </div>
      <a href={"#"} className=" rounded-pill mt-4 view-all d-block m-auto w-25">
        {" "}
        <b>Xem tất cả </b>{" "}
      </a>
    </div>
  );
};

export default Saleproduct;
