import React, { useEffect, useState } from "react";
import ProductColumn from "./productCulumn";
import { useDispatch, useSelector } from "react-redux";
import { getListProduct } from "../../redux/actions/product";
import { SpinnerCircular } from "spinners-react";
function Products() {
  const products = useSelector((state) => state.products.data);
  const loading = useSelector((state) => state.products.load);
  const [view, setView] = useState(false);
  const [data, setData] = useState([]);
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getListProduct());
  }, [dispatch]);

  useEffect(() => {
    setData(products);
  }, [products]);

  const changeViewColumn = () => {
    setView(true);
  };
  const changeViewList = () => {
    setView(false);
  };

  return (
    <div>
      <div className="left d-flex align-items-center ">
        <i
          class="list fas fa-bars  p-2 rounded-pill"
          onClick={() => changeViewColumn()}
        ></i>
        <i
          class="column fas fa-columns ms-xl-2 ms-lg-2 ms-md-2 p-2 rounded-pill"
          onClick={() => changeViewList()}
        ></i>
        <select className="form-select rounded-pill ms-xl-3 ms-lg-3 ms-md-3   fw-bold bg-light">
          <option value>Tất cả</option>
          <option value>Mới nhập hàng</option>
          <option value>Giá từ cao đến thấp</option>
          <option value>Giá từ thấp đến cao</option>
          <option value>Sắp xếp A &gt; Z</option>
          <option value>Sắp xếp Z &gt; A</option>
        </select>
      </div>
      {view ? (
        <div className=" ">
          {loading ? (
            <SpinnerCircular
              size={90}
              thickness={80}
              speed={150}
              color="rgb(255, 93, 0)"
              secondaryColor="rgb(47, 212, 234)"
              className="m-auto"
            />
          ) : data && (data || []).length > 0 ? (
            <>
              {" "}
              {(data || []).map((e) => (
                <ProductColumn data={e} />
              ))}
            </>
          ) : (
            <p>no data</p>
          )}
        </div>
      ) : (
        <div className="row ">
          {loading ? (
            <SpinnerCircular
              size={90}
              thickness={80}
              speed={150}
              color="rgb(255, 93, 0)"
              secondaryColor="rgb(47, 212, 234)"
              className="m-auto"
            />
          ) : data && (data || []).length > 0 ? (
            <>
              {" "}
              {(data || []).map((e) => (
                <ProductColumn data={e} />
              ))}
            </>
          ) : (
            <p>no data</p>
          )}
        </div>
      )}
    </div>
  );
}

export default Products;
