import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./productItem.css";
function ProductItem(props) {
  const [imgUrl, setImgUrl] = useState([]);
  const [price, setPrice] = useState([]);
  const [config, setConfig] = useState([]);
  useEffect(() => {
    props.data.HinhAnh.slice(0, 1).map((e) => setImgUrl(e.Url));

    props.data.CauHinhSanPham.splice(0, 1).map((e) =>
      setPrice(
        (((100 - e.PhanTramGiamGia) / 100) * e.GiaSanPham).toLocaleString(
          "vi-vn"
        ),
        setConfig(e)
      )
    );
  }, [props.data.HinhAnh, props.data.CauHinhSanPham]);
  return (
    <div
      className="col-xl-3 col-md-4 col-sm-6 col-6 product-box mb-3 mt-3"
      key={props.data._id}
    >
      <div className="card p-2 product-card ">
        <small className="bg-danger w-50 text-center p-1 rounded-pill">
          Giảm {config.PhanTramGiamGia}%
        </small>
        <Link className="product-tag" to={"/san-pham/" + props.data.Key}>
          <div className="img-box w-100">
            <img src={imgUrl} className="w-100" alt="" />
          </div>
        </Link>
        <div className="card-body">
          <h6 className="fw-bold">
            <Link
              className="text-decoration-none text-dark"
              to={"/san-pham/" + props.data.Key}
            >
              {" "}
              {props.data.TenSanPham}{" "}
            </Link>{" "}
          </h6>
          <h6 className="fw-bold mt-2 text-info">VNĐ {price}</h6>
        </div>
        <div className="hide ">
          Color: <b className="text-warning"> Gold </b>
          <p />
          <button className="w-100 btn-info btn text-light"> Add cart</button>
        </div>
      </div>
    </div>
  );
}

export default ProductItem;
