/* eslint-disable no-dupe-keys */
/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useState, useEffect } from "react";
import axios from "axios";
import Slider from "react-slick";

const Brand = (props) => {
  const settings = {
    className: "center",
    centerMode: true,
    infinite: true,
    speed: 500,
    slidesToShow: 5,
    slidesToScroll: 1,
    autoplaySpeed: 2000,
    autoplay: true,
    pauseOnHover: true,
    initialSlide: 0,
    nextArrow: <SampleNextArrow />,
    prevArrow: <SamplePrevArrow />,
    centerPadding: "20px",
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 1,
          autoplay: false,
          swipeToSlide: true,
          nextArrow: false,
          prevArrow: false,
        },
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 2,
          initialSlide: 2,
          autoplay: false,
          swipeToSlide: true,
          nextArrow: false,
          prevArrow: false,
        },
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          autoplay: false,
          swipeToSlide: true,
          nextArrow: false,
          prevArrow: false,
        },
      },
    ],
  };

  function SampleNextArrow(props) {
    const { className, style, onClick } = props;
    return (
      <div
        className={className}
        style={{
          ...style,
          display: "block",
          background: "black",
          marginRight: "1rem",
          zIndex: "2",
          borderRadius: "50%",
        }}
        onClick={onClick}
      />
    );
  }

  function SamplePrevArrow(props) {
    const { className, style, onClick } = props;
    return (
      <div
        className={className}
        style={{
          ...style,
          display: "block",
          background: "black",
          marginLeft: "1rem",
          zIndex: "2",
          borderRadius: "50%",
        }}
        onClick={onClick}
      />
    );
  }

  const url = "http://localhost:3100/sanpham";
  const [Data, setData] = useState([]);
  useEffect(() => {
    getData();
  }, []);

  const getData = async () => {
    try {
      await axios.all([axios.get(url)]).then(
        axios.spread((obj) => {
          setData(obj.data);
        })
      );
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <Slider {...settings}>
      {Data.map((e) => (
        <div class="card p-2 product-card " key={e.id}>
          <a class="mb-4 product-tag" href="#">
            <div class="img-box w-100">
              <img
                src="https://lumen.thinkpro.vn//backend/uploads/product/avatar/2021/9/15/macbook-pro-13-2020-1jpg"
                class="w-100"
                alt=""
              />
            </div>
          </a>
          <div class="card-body">
            <h6> {e.TenSanPham} </h6>
            <h6>
              <p>
                Giá từ : <b class="text-danger">200.000vnđ</b>
              </p>
            </h6>
            <p>
              <small>
                <del>300.000vnđ</del>
              </small>
            </p>

            <small>
              <i class="fas fa-gifts text-danger me-2"></i> Đang giảm giá
            </small>
          </div>

          <div class="hide-slide w-100">
            <p class="bg-light p-3 text-dark">
              Intel Core i5-1135G7, RAM 8GB, 512GB m.2 NVMe, 14" FHD IPS (1920 x
              1080), Intel Iris Xe, 48Wh, Pink (SF314-59-5178)
            </p>
            <div className="p-2">
              <button class="w-100 btn-info btn text-light rounded-pill fw-bold">
                Thêm vào giỏ
              </button>
            </div>
          </div>
        </div>
      ))}
    </Slider>
  );
};

export default Brand;
