import { POST_COMMENT_SUCCESS, POST_COMMENT } from "../constants/comment";

const postCommentReducer = (state = [], action) => {
  switch (action.type) {
    case POST_COMMENT:
      return {
        ...state,
        load: true,
      };

    case POST_COMMENT_SUCCESS:
      const { data } = action.payload;
      return {
        ...state,
        data: data,
        load: false,
      };
    default:
      return state;
  }
};

export default postCommentReducer;
