import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getListRate } from "../../redux/actions/rate";

import RateList from "./listrating";
import { SpinnerCircular } from "spinners-react";
import { getListOrder } from "../../redux/actions/order";
const Rating = (props) => {
  const dispatch = useDispatch();
  const data = useSelector((state) => state.rate.data);
  const load = useSelector((state) => state.rate.load);
  const order = useSelector((state) => state.order.data);
  const productID = props.product._id;
  const [accessRate, setAccess] = useState([]);
  console.log(accessRate);
  useEffect(() => {
    dispatch(getListRate(productID));
    dispatch(getListOrder(localStorage.idUser));
  }, [dispatch, productID]);

  useEffect(() => {
    (order || []).map((e) => {
      (e.SanPham || []).map((msp) => {
        if (msp.MaSanPham == productID) {
          setAccess(e._id);
        }
      });
    });
  }, []);

  return (
    <>
      <form action="" className="mb-5">
        <div className="form-group">
          <div className="float-end rating ">
            <label>
              <input type="radio" name="stars" value="1" required />
              <span class="icon">
                <i className="far fa-star"></i>
              </span>
            </label>
            <label>
              <input type="radio" name="stars" value="2" required />
              <span class="icon">
                <i className="far fa-star"></i>
                <i className="far fa-star"></i>
              </span>
            </label>
            <label>
              <input type="radio" name="stars" value="3" required />
              <span class="icon">
                <i className="far fa-star"></i>
                <i className="far fa-star"></i>
                <i className="far fa-star"></i>
              </span>
            </label>
            <label>
              <input type="radio" name="stars" value="4" required />
              <span class="icon">
                <i className="far fa-star"></i>
                <i className="far fa-star"></i>
                <i className="far fa-star"></i>
                <i className="far fa-star"></i>
              </span>
            </label>
            <label>
              <input
                type="radio"
                class="form-check-input"
                name="stars"
                value="5"
                required
              />
              <span class="icon">
                <i className="far fa-star"></i>
                <i className="far fa-star"></i>
                <i className="far fa-star"></i>
                <i className="far fa-star"></i>
                <i className="far fa-star"></i>
              </span>
            </label>
          </div>

          <textarea
            className="form-control"
            placeholder="Viết đánh giá"
            id="exampleFormControlTextarea1"
            rows="3"
            required
          ></textarea>

          {accessRate || [] ? (
            accessRate(
              <button
                type="submit"
                className="cart-btn mt-2 rounded-pill mt-1 px-3 py-2 float-end fw-bold"
                disabled
              >
                {" "}
                Gửi đánh giá{" "}
              </button>
            )
          ) : (
            <button
              type="submit"
              className="cart-btn mt-2 rounded-pill mt-1 px-3 py-2 float-end fw-bold"
            >
              {" "}
              Gửi đánh giá{" "}
            </button>
          )}
        </div>
      </form>
      {load ? (
        <SpinnerCircular
          size={70}
          thickness={80}
          speed={150}
          color="rgb(255, 93, 0)"
          secondaryColor="rgb(47, 212, 234)"
          className="m-auto d-block"
        />
      ) : (
        <>
          {(data || []).length == 0 ? (
            <h5 className="text-center p-3 ">
              {" "}
              <b>Sản phẩm chưa có đánh giá...</b>{" "}
            </h5>
          ) : (
            <>
              <h4 className="mt-5">
                <b href="" className=" ms-2">
                  <small>
                    <i class="fas fa-award me-2"></i> {(data || []).length} đánh
                    giá
                  </small>{" "}
                </b>{" "}
              </h4>
              <RateList rate={data} customer={props.customer} />

              <button className="cart-btn rounded-pill mt-4 px-3 py-2 m-auto d-block fw-bold">
                {" "}
                Xem Thêm đánh giá{" "}
              </button>
            </>
          )}
        </>
      )}
    </>
  );
};
export default Rating;
