/* eslint-disable jsx-a11y/alt-text */
import React from "react";
import Slider from "react-slick";

export const Product = () => {
  const settings = {
    customPaging: function (i) {
      return (
        <a>
          <img
            className="w-100"
            src={`https://lumen.thinkpro.vn//backend/uploads/product/avatar/2021/8/31/hp-340s-g7-thinkpro-thumbnailjpg`}
          />
        </a>
      );
    },
    dots: true,
    dotsClass: "slick-dot slick-thumb mb-5",
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    nextArrow: <SampleNextArrow />,
    prevArrow: <SamplePrevArrow />,
    responsive: [
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          swipeToSlide: true,
          nextArrow: false,
          prevArrow: false,
        },
      },
    ],
  };
  function SampleNextArrow(props) {
    const { className, style, onClick } = props;
    return (
      <div
        className={className}
        style={{
          ...style,
          display: "block",
          background: "rgba(194, 191, 191, 0.911)",
          marginRight: "1rem",
          zIndex: "2",
          borderRadius: "50%",
        }}
        onClick={onClick}
      />
    );
  }

  function SamplePrevArrow(props) {
    const { className, style, onClick } = props;
    return (
      <div
        className={className}
        style={{
          ...style,
          display: "block",
          background: "rgba(194, 191, 191, 0.911)",
          marginLeft: "1rem",
          zIndex: "2",
          borderRadius: "50%",
        }}
        onClick={onClick}
      />
    );
  }

  return (
    <div className="w-75 m-auto d-block">
      <Slider {...settings}>
        <div>
          <img
            className="w-100"
            src={
              "https://lumen.thinkpro.vn//backend/uploads/product/avatar/2021/8/31/hp-340s-g7-thinkpro-thumbnailjpg"
            }
          />
        </div>
        <div>
          <img
            className="w-100"
            src={
              "https://lumen.thinkpro.vn//backend/uploads/product/avatar/2021/8/31/hp-340s-g7-thinkpro-thumbnailjpg"
            }
          />
        </div>
        <div>
          <img
            className="w-100"
            src={
              "https://lumen.thinkpro.vn//backend/uploads/product/avatar/2021/8/31/hp-340s-g7-thinkpro-thumbnailjpg"
            }
          />
        </div>
      </Slider>
    </div>
  );
};
