/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useEffect } from "react";
import Breadcrumb from "../../constants/breadcrumb";
import ProductImg from "./productImg_slide";
import ProductConfig from "./product_config";
import Review from "./detailReview";
import Rating from "./rating";
import Comment from "./comment";
import "./index.css";
const Detail = () => {
  useEffect(() => {
    key();
  }, []);
  const key = () => {
    const key = window.location.pathname
      .split("/san-pham/")
      .splice(1)
      .toString();
    console.log(key);
  };

  return (
    <main className="">
      <div className="container">
        <div className="pt-3">
          <Breadcrumb
            key=""
            currentPage="Chi tiết Laptop"
            listBread={[
              {
                path: "/",
                pageName: "Trang chủ",
              },
            ]}
          />
        </div>
      </div>

      <div>
        <div className="row ">
          <div className="col-xl-6 col-lg-6 col-md-12 col-sm-12 img-slider">
            <ProductImg />
          </div>
          <div className="col-xl-6 col-lg-6 col-md-12 col-sm-12 ">
            <ProductConfig />
          </div>
        </div>
      </div>
      <div className="mt-5 mb-3">
        <h2>
          <b>Đánh giá chi tiết</b>{" "}
        </h2>
        <Review />
        <button className="addcart-btn  rounded-pill mt-4 view-all">
          {" "}
          <b>Xem Thêm nội dung</b>{" "}
        </button>
      </div>

      <div className="mt-3 mb-3" id="danhgia">
        <h2>
          <b>Đánh giá của khách hàng</b>{" "}
        </h2>
        <Rating />
        <button className="addcart-btn rounded-pill mt-4 view-all">
          {" "}
          <b>Xem Thêm đánh giá</b>{" "}
        </button>
      </div>

      <div className="mt-5 mb-3" id="binhluan">
        <h2>
          <b>Bình luận về sản phẩm</b>{" "}
        </h2>
        <Comment />
        <button className="addcart-btn  rounded-pill mt-4 view-all">
          {" "}
          <b>Xem Thêm bình luận</b>{" "}
        </button>
      </div>
    </main>
  );
};
export default Detail;
