/* eslint-disable no-dupe-keys */
/* eslint-disable jsx-a11y/anchor-is-valid */
import React from "react";
import Slider from "react-slick";

const Slider_home = (props) => {
  const settings = {
    dots: true,
    infinite: true,
    slidesToShow: 3,
    slidesToScroll: 1,
    autoplay: true,
    speed: 2000,
    autoplaySpeed: 2000,
    cssEase: "linear",
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 1,
          nextArrow: false,
          prevArrow: false,
        },
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 2,
          initialSlide: 2,

          nextArrow: false,
          prevArrow: false,
          dots: false,
        },
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          nextArrow: false,
          prevArrow: false,
          dots: false,
        },
      },
    ],
  };

  function SampleNextArrow(props) {
    const { className, style, onClick } = props;
    return (
      <div
        className={className}
        style={{
          ...style,
          display: "block",
          background: "black",
          marginRight: "1rem",
          zIndex: "2",
          borderRadius: "50%",
        }}
        onClick={onClick}
      />
    );
  }

  function SamplePrevArrow(props) {
    const { className, style, onClick } = props;
    return (
      <div
        className={className}
        style={{
          ...style,
          display: "block",
          background: "black",
          marginLeft: "1rem",
          zIndex: "2",
          borderRadius: "50%",
        }}
        onClick={onClick}
      />
    );
  }

  return (
    <div className="rounded-3 w-100">
      <Slider {...settings}>
        <div>
          <img
            className="rounded-3 w-100"
            src={
              "https://lumen.thinkpro.vn//backend/uploads/banner/2021/9/8/808x288-2%20copy.jpg"
            }
          />
        </div>
        <div>
          <img
            className="rounded-3 w-100"
            src={
              "https://lumen.thinkpro.vn//backend/uploads/banner/2021/8/26/808x288%20copy.jpg"
            }
          />
        </div>
        <div>
          <img
            className="rounded-3 w-100"
            src={
              "https://lumen.thinkpro.vn//backend/uploads/banner/2021/8/15/KV%20back2school-808x288%20copy.jpg"
            }
          />
        </div>
        <div>
          <img
            className="rounded-3 w-100"
            src={
              "https://lumen.thinkpro.vn//backend/uploads/banner/2021/9/7/Trung%20thu%20808x288.jpg"
            }
          />
        </div>
      </Slider>
    </div>
  );
};

export default Slider_home;
