import React, { useEffect } from "react";

const CommentList = (props) => {
  useEffect(() => {
    const repElement = document.querySelectorAll(".showreply");
    const hideElement = document.querySelectorAll("#hide");
    for (let i = 0; i < repElement.length; i++) {
      repElement[i].addEventListener("click", () => {
        hideElement[i].style.display = "block";
        repElement[i].classList.add("is-active");
        repElement[i].classList.remove("showreply");
      });
    }
  }, []);

  return (
    <div className="row">
      {(props.comment || []).map((e) =>
        (props.customer || []).map((kh) => (
          <>
            {e.MaKhachHang == kh._id ? (
              <>
                <div
                  className="col-xl-2 col-lg-2 col-md-2 col-sm-2  "
                  key={e._id}
                >
                  {kh.Avatar == "" ? (
                    <img
                      className="rounded-circle d-block m-auto mt-3"
                      style={{ width: "4rem", height: "4rem" }}
                      src="https://cdn-icons-png.flaticon.com/512/147/147144.png"
                      alt=""
                    />
                  ) : (
                    <img
                      className="rounded-circle d-block m-auto mt-3"
                      style={{ width: "4rem", height: "4rem" }}
                      src={kh.Avatar}
                      alt=""
                    />
                  )}
                </div>
                <div className="col-xl-10 col-lg-10 col-md-10 col-sm-10">
                  <div className="d-flex align-items-center">
                    {" "}
                    <span className="me-2">
                      <b className="fw-bold">{kh.TenKhachHang}</b>{" "}
                    </span>
                  </div>

                  <p className="mt-2">{e.NoiDungBinhLuan}</p>
                  <a className="text-info fw-bold showreply">Trả lời</a>

                  <span className="ms-3">{e.createdAt}</span>

                  <form action="" className="mb-5">
                    <div
                      id="hide"
                      className="form-group"
                      style={{ display: "none" }}
                    >
                      <textarea
                        className="form-control"
                        placeholder="Viết bình luận"
                        rows="3"
                        required
                      ></textarea>

                      <button
                        type="submit"
                        className="cart-btn mt-2 rounded-pill mt-1 py-2 float-end"
                      >
                        {" "}
                        <b>Gửi</b>{" "}
                      </button>
                    </div>
                  </form>

                  <hr />

                  {(e.PhanHoi || []).map((rep) =>
                    (props.customer || []).map((repper) => (
                      <>
                        {rep.MaKhachHang == repper._id ? (
                          <div className="row">
                            <div className="col-2  ">
                              {repper.Avatar == "" ? (
                                <img
                                  className="rounded-circle mt-3 m-auto d-block"
                                  style={{ width: "4rem", height: "4rem" }}
                                  src="https://cdn-icons-png.flaticon.com/512/147/147144.png"
                                  alt=""
                                />
                              ) : (
                                <img
                                  className="rounded-circle d-block m-auto mt-3"
                                  style={{ width: "4rem", height: "4rem" }}
                                  src={repper.Avatar}
                                  alt=""
                                />
                              )}
                            </div>
                            <div className="col-9 ms-3">
                              <div className="d-flex align-items-center">
                                {" "}
                                <span className="me-2">
                                  <b className="fw-bold">
                                    {repper.TenKhachHang}
                                  </b>{" "}
                                </span>
                              </div>

                              <p className="mt-2">{rep.NoiDungTraLoi}</p>
                              <span className="">{rep.ThoiGian}</span>
                              <hr />
                            </div>
                          </div>
                        ) : (
                          <p></p>
                        )}
                      </>
                    ))
                  )}

                  {(e.PhanHoi || []).map((rep) =>
                    (props.staff || []).map((repper) => (
                      <>
                        {rep.MaKhachHang == repper._id ? (
                          <div className="row">
                            <div className="col-2  ">
                              {repper.Avatar == "" ? (
                                <img
                                  className="rounded-circle mt-3 m-auto d-block"
                                  style={{ width: "4rem", height: "4rem" }}
                                  src="https://cdn-icons-png.flaticon.com/512/147/147144.png"
                                  alt=""
                                />
                              ) : (
                                <img
                                  className="rounded-circle d-block m-auto mt-3"
                                  style={{ width: "4rem", height: "4rem" }}
                                  src={repper.Avatar}
                                  alt=""
                                />
                              )}
                            </div>
                            <div className="col-9 ms-3">
                              <div className="d-flex align-items-center">
                                {" "}
                                <span className="me-2">
                                  <b className="fw-bold">
                                    Nhân viên {repper.TenNhanVien} trả lời
                                  </b>{" "}
                                </span>
                              </div>

                              <p className="mt-2">{rep.NoiDungTraLoi}</p>
                              <span className="">{rep.ThoiGian}</span>
                              <hr />
                            </div>
                          </div>
                        ) : (
                          <p></p>
                        )}
                      </>
                    ))
                  )}
                </div>
              </>
            ) : (
              <p></p>
            )}
          </>
        ))
      )}
    </div>
  );
};

export default CommentList;
