import React, { useEffect, useState } from "react";
import ProductItem from "./productItem";
import { useDispatch, useSelector } from "react-redux";
import { getListProduct } from "../../redux/actions/product";
import { SpinnerCircular } from "spinners-react";
function Products() {
  const data = useSelector((state) => state.products.data);
  const loading = useSelector((state) => state.products.load);

  const [view] = useState(true);

  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getListProduct());
  }, [dispatch]);

  const changeViewColumn = () => {
    console.log(view);
  };

  return (
    <div>
      <div className="left d-flex align-items-center ">
        <i
          class="column fas fa-columns p-2 rounded-pill"
          onClick={() => changeViewColumn()}
        ></i>
        <i class="list fas fa-bars ms-2 p-2 rounded-pill"></i>
        <select className="form-select rounded-pill ms-3">
          <option value>Tất cả</option>
          <option value>Mới nhập hàng</option>
          <option value>Giá từ cao đến thấp</option>
          <option value>Giá từ thấp đến cao</option>
          <option value>Sắp xếp A &gt; Z</option>
          <option value>Sắp xếp Z &gt; A</option>
        </select>
      </div>
      <div className="row ">
        {loading ? (
          <SpinnerCircular
            size={90}
            thickness={80}
            speed={150}
            color="rgb(255, 93, 0)"
            secondaryColor="rgb(47, 212, 234)"
            className="m-auto"
          />
        ) : data && (data || []).length > 0 ? (
          <>
            {" "}
            {(data || []).map((e) => (
              <ProductItem data={e} />
            ))}
          </>
        ) : (
          <p>no data</p>
        )}
      </div>
    </div>
  );
}

export default Products;
