import React, { useState, useEffect } from "react";
import axios from "axios";
import Slider from "react-slick";

const Saleproduct = () => {
  const url = "http://localhost:3100/sanpham";

  const [Data, setData] = useState([]);
  useEffect(() => {
    getData();
  }, []);

  const getData = async () => {
    try {
      await axios.all([axios.get(url)]).then(
        axios.spread((obj) => {
          console.log(obj.data);
          setData(obj.data);
        })
      );
    } catch (error) {
      console.log(error);
    }
  };

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
  };
  return (
    <div>
      <h2> Single Item</h2>
      <Slider {...settings}>{Data.map((e) => 1)}</Slider>
    </div>
  );
};

export default Saleproduct;
