import React, { useState, useEffect } from "react";
import axios from "axios";
import InfiniteCarousel from "react-leaf-carousel";

const Saleproduct = () => {
  const url = "http://localhost:3100/sanpham";

  useEffect(() => {
    getData();
  }, []);
  const getData = async () => {
    try {
      await axios.all([axios.get(url)]).then(
        axios.spread((obj) => {
          console.log(obj.data);
        })
      );
    } catch (error) {
      console.log(error);
    }
  };
  return (
    <InfiniteCarousel
      breakpoints={[
        {
          breakpoint: 500,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1,
          },
        },
        {
          breakpoint: 800,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 2,
          },
        },
      ]}
      showSides={true}
      sidesOpacity={10}
      sideSize={0}
      slidesToScroll={3}
      slidesToShow={5}
      lazyLoad={true}
    >
      <div className="card p-2 product-card">
        <a className="mb-4 product-tag" href="#">
          <div className="img-box w-100">
            <img
              src="https://lumen.thinkpro.vn//backend/uploads/product/avatar/2020/11/20/swift514_00gold.jpg"
              className="w-100"
              alt=""
            />
          </div>
          <h5>Laptop Acer 55(Chính hãng)</h5>
        </a>
        <div className="card-body">
          <h5>
            <p>
              Giá từ : <b className="text-danger">200.000vnđ</b>
            </p>
          </h5>
          <p>
            <del>300.000vnđ</del>
          </p>

          <p>
            <i className="fas fa-gifts text-danger me-2"></i> Đang giảm giá
          </p>
        </div>

        <div className="hide-slide  w-100">
          <p className=" bg-light p-3">
            Intel Core i5-1135G7, RAM 8GB, 512GB m.2 NVMe, 14" FHD IPS (1920 x
            1080), Intel Iris Xe, 48Wh, Pink (SF314-59-5178)
          </p>
          <div className="p-3">
            <button className="w-100 btn-info btn text-light rounded-pill ">
              Thêm vào giỏ
            </button>
          </div>
        </div>
      </div>
    </InfiniteCarousel>
  );
};

export default Saleproduct;
